mapper.select:
SELECT gc.id AS gcId, gc.gc_no AS gcNo, gc.good_name AS goodName, gc.good_forshort AS goodForshort, gc.good_attribute AS goodAttribute, gc.good_type AS goodType, gc.good_property AS goodProperty, gc.good_shape AS goodShape, gc.good_category_id AS goodCategoryId, gc.good_status AS goodStatus, gc.review_id AS reviewId, gc.review_status AS reviewStatus, gc.review_remarks AS reviewRemarks, gc.create_id AS createId, gc.update_id AS updateId, gc.create_time AS createTime, gc.update_time AS updateTime, gcs.id AS gcsId, gcs.gcs_no AS gcsNo, gcs.sku_name AS skuName, gcs.gaa_id AS gaaId, gcs.sd_id AS sdId, gcs.default_amount AS defaultAmount, gcs.noticket_amount AS noticketAmount, gcs.ticket_someamount AS ticketSomeamount, gcs.noticket_someamount AS noticketSomeamount, gcs.ticket_amount AS ticketAmount FROM goods_card gc, goods_card_sku gcs
gc.`id` = gcs.`gc_id`
and gc.gc_no like concat('%', #{queryWrapper.gcNo}, '%')
and gcs.`gcs_no` like concat('%', #{queryWrapper.gcsNo}, '%')
and gcs.`id` = #{queryWrapper.gcsId}
and gc.`good_name` like concat('%', #{queryWrapper.goodName}, '%')
and gc.`good_type` = #{queryWrapper.goodType}
and gc.`good_property` = #{queryWrapper.goodProperty}
and gc.`good_shape` = #{queryWrapper.goodShape}
and gc.`good_category_id` = #{queryWrapper.goodCategoryId}
and gc.`good_status` = #{queryWrapper.goodStatus}
and gc.`review_status` = #{queryWrapper.reviewStatus}
